import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

export default function PriceChart({ chartData }) {
  if(!chartData || chartData.length===0) return <div>No chart data available</div>;
  const labels = chartData.map(r=>String(r.year || r["year"] || r[0] || ''));
  const values = chartData.map(r => {
    if (r == null) return null;
    if (typeof r.value === 'number') return r.value;
    if (typeof r.price === 'number') return r.price;
    if (typeof r.demand === 'number') return r.demand;
    // fallback: find first numeric property
    for (const k in r) {
      if (typeof r[k] === 'number') return r[k];
    }
    return null;
  });
  const datasetColor = 'rgba(0,163,255,0.95)';
  const data = { labels, datasets: [{ label:'Average Price', data: values, fill:false, tension:0.2, borderColor: datasetColor, backgroundColor: 'rgba(0,163,255,0.18)', pointBackgroundColor: '#00A3FF', pointBorderColor: '#071423', pointRadius: 4, borderWidth: 2 }] };
  const options = {
    plugins: { legend: { labels: { color: '#BFD7FF' } }, tooltip: { bodyColor: '#E6EEF8', titleColor: '#E6EEF8' } },
    scales: {
      x: { ticks: { color: '#BFD7FF' }, grid: { color: 'rgba(255,255,255,0.03)' } },
      y: { ticks: { color: '#BFD7FF' }, grid: { color: 'rgba(255,255,255,0.03)' } }
    },
    maintainAspectRatio: false,
    responsive: true
  };
  return <div style={{height:280}}><Line data={data} options={options} /></div>;
}
