import React from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
ChartJS.register(ArcElement, Tooltip, Legend);

export default function PieChartComp({ rows }) {
  if(!rows || rows.length===0) return <div>No data</div>;
  const counts = {};
  rows.forEach(r => {
    const a = (r.area || r['final location'] || 'Unknown');
    const k = String(a).trim();
    counts[k] = (counts[k] || 0) + 1;
  });
  const keys = Object.keys(counts).slice(0,6);
  const values = keys.map(k=>counts[k]);
  const colors = ['#00A3FF','#FF7A59','#7C5CFF','#00D166','#FFD166','#FF5DA2'];
  const data = { labels: keys, datasets: [{ data: values, backgroundColor: colors.slice(0, keys.length), hoverOffset: 6, borderColor: '#071423' }] };
  const options = { plugins: { legend: { labels: { color: '#BFD7FF' } } }, maintainAspectRatio: false, responsive: true };
  return <div style={{height:220}}><Pie data={data} options={options} /></div>;
}
